mapper.select:
SELECT SCI.SCHOOL_CODE AS SCHOOL_CODE ,SSI.SCHOOL_NAME AS SCHOOL_NAME ,SCI.CLASS_CODE AS CLASS_CODE ,SCI.CLASS_NAME AS CLASS_NAME FROM ST_CLASS_INFO SCI LEFT OUTER JOIN ST_SCHOOL_INFO SSI ON SCI.SCHOOL_CODE = SSI.SCHOOL_CODE WHERE 1=1
AND SCI.SCHOOL_CODE = #{searchData}
ORDER BY SSI.SCHOOL_CODE ASC, CLASS_CODE ASC

mapper.update:

UPDATE ST_SCHOOL_INFO
SET
	USER_SEQ_NO  = #{userSeqNo}
WHERE 1=1
AND SCHOOL_CODE= #{schoolCode}
